import { useState } from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar';


const Analysis = ({isSelected, totalWeight, bodyWeight, langauge, gender}) => {
    const [swr, setSwr] = useState(0);
    //setSwr({totalWeight}/{bodyWeight});
    
    function StackedExample(now) { 
        return (
          <ProgressBar>
            <ProgressBar striped variant="secondary" now={((totalWeight/bodyWeight) * 100).toFixed(2)} key={1} label={`${((totalWeight/bodyWeight) * 100).toFixed(2)}%`}/>
            <ProgressBar striped variant="primary" now={(100 - (totalWeight/bodyWeight) * 100).toFixed(2)} key={2} />
          </ProgressBar>
        );
      }
      function SwitchLevel(percentage) {
        switch(percentage) {
          case percentage < 120:
            return langauge?"необученный":'Untrained';
          case  percentage>= 120 || percentage<= 175:
            return langauge?"Новичок":'Novice';
          case  percentage>= 175 || percentage<= 210:
            return langauge?"Средний":'Intermediate';
          case  percentage>= 210:
            return langauge?"Элита":'Elite';
          default:
            return 'error';
        }
      }
      function SwitchDistrib(percentage) {
        switch(percentage) {
          case percentage < 120:
            return '50th';
          case  percentage>= 120 || percentage<= 175:
            return '80th';
          case  percentage>= 175 || percentage<= 210:
            return '90th';
          case  percentage>= 210:
            return '99th';
          default:
            return 'error';
        }
      }
      function SwitchUpperLevel(percentage) {
          switch(percentage) {
            case percentage < 120:
              return langauge?"Новичок":'Novice';
            case  percentage>= 120 || percentage<= 175:
              return langauge?"Средний":'Intermediate';
            case  percentage>= 175:
              return langauge?"Элита":'Elite';
            default:
              return 'error';
        }
      }
    return ( 
        <div className="container">
            <div className='my-5 text-center'>
         <h3 className='my-2'>{langauge?"Ты можешь сделать":'You can'} {isSelected} {((totalWeight/bodyWeight) * 100).toFixed(2)} % {langauge?"веса вашего тела":'of your body weight'}</h3>
         <h5 className='my-2'>{langauge?"Помещение вас в":'Putting you in the'}  {SwitchDistrib(totalWeight/bodyWeight *100)} {langauge?"процентиль":'percentile'}</h5>
         {console.log(totalWeight/bodyWeight * 100)}
         
         
            <div className="row my-3">
                <div className="col col-md-auto text-left">{SwitchLevel(totalWeight/bodyWeight *100)}</div>   
                <div className='col'>{StackedExample(66)}</div>
                <div className="col col-md-auto text-right">{SwitchUpperLevel(totalWeight/bodyWeight *100)}</div>
            </div>

            <h4>{langauge?"Ваш предполагаемый уровень физической подготовки:":'Your estimated level of fitness is:'} {SwitchLevel(totalWeight/bodyWeight *100)}</h4>

        </div>
        </div>
     );
}
 
export default Analysis;
